SELECT c.id, c.project_id as projectId, c.user_id as userId, c.parent_id as parentId, c.reply_user_id as replyUserId, c.content, c.create_time as createTime, c.status, c.is_donor as isDonor, u1.nick_name as nickName, u1.avatar as avatar, u2.nick_name as replyNickName FROM project_comments c LEFT JOIN users u1 ON c.user_id = u1.id LEFT JOIN users u2 ON c.reply_user_id = u2.id WHERE c.status >= 0 AND c.parent_id IN
#{id}
ORDER BY c.create_time ASC
